fn bubble_sort(arr: &mut [i32]) {
    let mut swapped = true;
    while swapped {
        swapped = false;
        for i in 0..arr.len() - 1 {
            if arr[i] > arr[i + 1] {
                std::mem::swap(arr[i], arr[i + 1]);
                swapped = true;
            }
        }
    }
}

