fn bubble_sort(arr: &mut [i32]) {
    // الخطوة 1: ضع علم التبديل على القيمة الصحيحة
    let mut swapped = true;

    // الخطوة 2: كرر حتى لا تكون هناك حاجة لمزيد من التبادلات
    while swapped {
        // الخطوة 3: تحقق مما إذا كانت هناك عناصر غير مرتبة
        for i in 0..arr.len() - 1 {
            if arr[i] > arr[i + 1] {
                // الخطوة 4: قم بتبديل العناصر
                std::mem::swap(arr[i], arr[i + 1]);
                // الخطوة 5: ضع علم التبديل مرة أخرى على القيمة الصحيحة
                swapped = true;
            }
        }
    }
}

